mapper.select:
SELECT COUNT(1) > 0
FROM user_likes
WHERE liker_id = #{likerId}
  AND liked_id = #{likedId}

mapper.delete:
DELETE FROM user_likes
WHERE liker_id = #{likerId}
  AND liked_id = #{likedId}

mapper.select:
SELECT COUNT(DISTINCT liked_id)
FROM user_likes
WHERE liker_id = #{likerId}
  AND liked_at >= #{start}
  AND liked_at <= #{end}
